//Header/index.tsx
import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../store';
import SearchBar from '../Bars/SearchBar/SearchBar';
import CartIcon from "../../assets/images/carrinho.png";
import { Header1, Nav, HeaderLink, SearchBarDiv, StyledIconWrapper, Img, CartItemCount, P } from './styles';
import { Link } from 'react-router-dom';

const Header: React.FC = () => {
const products = useSelector((state: RootState) => state.cart.products);
const count = Object.keys(products).length;

return (
<Header1>
<div>
<Nav>
<HeaderLink texto="Ecommerce" redirect="/Cart" />
<HeaderLink texto="Shop" redirect="/Carrinho" />
<HeaderLink texto="Stories" redirect="/produtos" />
<HeaderLink texto="About" redirect="/about" />

      {/* <SearchBarDiv>
        <SearchBar />
      </SearchBarDiv> */}

      <StyledIconWrapper>
        <Link to="/cart">
          <Img src={CartIcon}></Img>
          {count > 0 && <CartItemCount>{count}</CartItemCount>}
        </Link>
      </StyledIconWrapper>

      <HeaderLink texto="Login" redirect="/" />
      <P>adicionar função de login</P>
    </Nav>
  </div>
</Header1>
);
};

export default Header;
