import styled from "styled-components";
import Link from '../Link';
import { colors } from "../../styles/themes";

export const P = styled.p`
font-size: 2rem;
`;

export const EmptyCart = styled.div`
text-align: center;
padding: 5% 0%;
`;

export const DivEmpty = styled.div`
margin-bottom: 3%;
`;

export const Shopping = styled(Link)`
text-decoration: none;
border: none;
`;

export const ButtonEmpty = styled.button`
padding: 10px 60px;
margin-bottom: 10%;
border: none;
/* border-radius: 50px; */
background-color: orange;
color: #fff;
font-size: 1.6rem;
  &:hover {
    transition: 0.5s;
    background-color: rgba(255, 165, 0, 0.7);
    cursor: pointer;
  }
`;

export const H3 = styled.h3`
font-size: 1.6rem;
`;

export const NoReady = styled(Link)`
text-decoration: none;
font-size: 1rem;
`;

export const DivFlex = styled.div`
display: flex;
justify-content: space-around;
`;

export const DivUl = styled.ul`
list-style: none;
width: 38%;
margin: 5% 0%;
text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.36);
`;

export const LiUnit = styled.li`
padding: 5% 0;
margin: 7% 0%;
border-block-end: 1px solid #f1f1f1;
`;

export const DivDetails = styled.div`
display: flex;
justify-content: center;
flex-direction: column;
background-color: ${colors.TopDown}
`;

export const B = styled.b`
margin-right: 1%;
`;

export const Button = styled.button`
margin: 1% 0%;
`;
