import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface Product {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

interface CartState {
  products: { [key: number]: Product };
}

const initialState: CartState = {
  products: {},
};

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    setProducts: (state, action: PayloadAction<{ [key: number]: Product }>) => {
      state.products = action.payload;
    },
    addProductToCart: (state, action: PayloadAction<Product>) => {
      const { id } = action.payload;
      state.products[id] = action.payload;
    },
    updateProductQuantity: (state, action: PayloadAction<{ productId: number; quantity: number }>) => {
      const { productId, quantity } = action.payload;
      const product = state.products[productId];
      if (product) {
        product.quantity = quantity;
      }
    },
    removeProduct: (state, action: PayloadAction<number>) => {
      const productId = action.payload;
      delete state.products[productId];
    },
  },
});

export const { setProducts, addProductToCart, updateProductQuantity, removeProduct } = cartSlice.actions;

export const cartReducer = cartSlice.reducer;
