// Cart/index.tsx
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../store';
import { updateProductQuantity, removeProduct } from '../Cart/cart';
import Header from '../Header';
import Footer from '../Footer';
import { P, H3, B } from './styles';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
  category: string;
  quantity: number;
}

const Cart: React.FC = () => {
  const products = useSelector((state: RootState) => state.cart.products);
  const productList = Object.values(products);
  const dispatch = useDispatch();

  const [removedItemCount, setRemovedItemCount] = useState(0);

  const handleIncreaseQuantity = (productId: number) => {
    const product = products[productId];
    if (product) {
      const updatedQuantity = product.quantity + 1;
      dispatch(updateProductQuantity({ productId, quantity: updatedQuantity }));
    }
  };

  const handleDecreaseQuantity = (productId: number) => {
    const product = products[productId];
    if (product) {
      const updatedQuantity = Math.max(product.quantity - 1, 0);
      dispatch(updateProductQuantity({ productId, quantity: updatedQuantity }));
    }
  };

  const handleRemoveProduct = (productId: number) => {
    dispatch(removeProduct(productId));
    setRemovedItemCount(removedItemCount + 1);
  };

  const calculateTotal = () => {
    return productList.reduce((acc, item) => acc + Number(item.price) * item.quantity, 0);
  };

  useEffect(() => {
    const total = calculateTotal();
    document.title = `Carrinho (${productList.length}) - Total: ${total}$`;

    localStorage.setItem('cart', JSON.stringify(products));
  }, [productList, products]);

  return (
    <div>
      <Header />

      {productList.length === 0 ? (
        <H3>Carrinho Vazio</H3>
      ) : (
        <>
          <H3>Produtos no Carrinho</H3>
          <ul>
            {productList.map((product: Product) => (
              <li key={product.id}>
                <div>
                  <P>{product.name}</P>
                  <P>Quantidade: {product.quantity}</P>
                </div>
                <div>
                  <button onClick={() => handleIncreaseQuantity(product.id)}>+</button>
                  <button onClick={() => handleDecreaseQuantity(product.id)}>-</button>
                  <button onClick={() => handleRemoveProduct(product.id)}>Remover</button>
                </div>
              </li>
            ))}
          </ul>
          <B>Total: {calculateTotal()}$</B>
          <P>Itens removidos: {removedItemCount}</P>
        </>
      )}

      <Footer />
    </div>
  );
};

export default Cart;
