interface SuccessPageProps {
  items: string[]; // Array de itens comprados
}

const SuccessPage: React.FC<SuccessPageProps> = ({ items }) => {
  const history = useHistory();

  useEffect(() => {
    // Redirecionar para a página inicial após 5 segundos
    const redirectTimeout = setTimeout(() => {
      history.push('/');
    }, 5000);

    return () => clearTimeout(redirectTimeout); // Limpar o timeout quando o componente é desmontado
  }, [history]);

  return (
    <div>
      <h1>Pedido criado com sucesso!</h1>
      <p>Itens comprados:</p>
      <ul>
        {items.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
      <p>Você será redirecionado para a página inicial em 5 segundos.</p>
    </div>
  );
};

export default SuccessPage;
