import styled from "styled-components";
import Link from "../../components/Link";
import { colors } from "../../styles/themes";

export const Teste = styled.div`
  display: flex;
  justify-content: space-evenly;
  padding: 20px;
`;

export const DivSucesso = styled.div`
  text-align: center;
  margin-bottom: 30px;

  h1 {
    font-size: 28px;
    margin-bottom: 10px;
    color: orange;
  }

  p {
    font-size: 18px;
    color: blue;
    margin-bottom: 10px;
  }
`;

export const Back = styled(Link)`
  display: inline-block;
  background-color: orange;
  color: #fff;
  padding: 10px 20px;
  border-radius: 4px;
  font-size: 16px;
  text-decoration: none;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: darkorange;
  }
`;

export const DivUl = styled.ul`
  list-style: none;
  padding: 0;
`;

export const LiUnit = styled.li`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  margin-bottom: 10px;
  background-color: green;
  color: blue;
  border-radius: 4px;

  &:last-child {
    margin-bottom: 0;
  }

  div {
    flex: 1;

    h1 {
      font-size: 18px;
      margin-bottom: 5px;
      color: orange;
    }

    p {
      font-size: 14px;
      margin-bottom: 3px;
    }
  }

  b {
    font-size: 16px;
  }
`;
