import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { AnyAction } from 'redux';
import { RootState } from '../store';
import {
  addProductToCart,
  removeProduct,
  updateProductQuantity,
} from './cart';
import Header from '../Header';
import { P, H3, B } from './styles';
import Footer from '../Footer';

const Cart: React.FC = () => {
  const products = useSelector((state: RootState) => state.cart.products);
  const productList = Object.values(products);
  const dispatch = useDispatch();

  const handleIncreaseQuantity = (productId: number) => {
    const product = products[productId];
    if (product) {
      const updatedQuantity = product.quantity + 1;
      dispatch(updateProductQuantity({ productId, quantity: updatedQuantity }));
    }
  };

  const handleDecreaseQuantity = (productId: number) => {
    const product = products[productId];
    if (product) {
      const updatedQuantity = Math.max(product.quantity - 1, 1);
      dispatch(updateProductQuantity({ productId, quantity: updatedQuantity }));
    }
  };

  const handleRemoveProduct = (productId: number) => {
    dispatch(removeProduct(productId));
  };

  const calculateTotal = () => {
    return productList.reduce(
      (acc, item) => acc + Number(item.price) * item.quantity,
      0
    );
  };

  useEffect(() => {
    const saveCartState = () => {
      const cartState = { products: productList };
      dispatch({ type: 'SAVE_CART_STATE', payload: cartState } as AnyAction);
    };

    saveCartState();
  }, [productList, dispatch]);

  return (
    <div>
      <Header />

      {productList.length === 0 ? (
        <P>Carrinho vazio</P>
      ) : (
        <div>
          <h2>Seu carrinho:</h2>
          {productList.map((product) => (
            <div key={product.id}>
              <h3>{product.name}</h3>
              <div>
                <button onClick={() => handleDecreaseQuantity(product.id)}>âˆ’</button>
                <input
                  type="number"
                  value={product.quantity}
                  min={1}
                  readOnly
                />
                <button onClick={() => handleIncreaseQuantity(product.id)}>+</button>
              </div>
              <p>$ {product.price}</p>
              <button onClick={() => handleRemoveProduct(product.id)}>
                Remover
              </button>
            </div>
          ))}

          <section>
            <H3>Detalhes do pedido</H3>
            <h4>{`Itens: (${productList.length})`}</h4>
            <B>Total</B>
            <b>$ {calculateTotal()}</b>
          </section>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default Cart;
